export default function Validate(values) {

    let errors = {}

    if(!values.name) {
        errors.name = 'Name required.'
    } else if(!/^[a-z][A-Z]+$/.test(values.name)) {
        errors.name = 'Name is not a number baby.'
    }

    return errors;

}
